import "./App.css";

import React, { useEffect, useRef } from "react";
import * as THREE from "three";

function HouseModel() {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;

    // Создание сцены
    const scene = new THREE.Scene();

    // Создание камеры
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / (window.innerHeight / 2), // Используем половину высоты окна
      0.1,
      1000
    );
    camera.position.z = 5;

    // Создание рендерера
    const renderer = new THREE.WebGLRenderer({ canvas });
    renderer.setSize(window.innerWidth, window.innerHeight / 2); // Используем половину высоты окна

    // Создание геометрии и материала для дома
    const geometry = new THREE.BoxGeometry(1, 1, 1);
    const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
    const cube = new THREE.Mesh(geometry, material);

    // Добавление дома на сцену
    scene.add(cube);

    // Анимация
    function animate() {
      requestAnimationFrame(animate);

      cube.rotation.x += 0.01;
      cube.rotation.y += 0.01;

      renderer.render(scene, camera);
    }

    animate();

    // Обновление размеров рендерера при изменении размеров окна
    function handleResize() {
      camera.aspect = window.innerWidth / (window.innerHeight / 2);
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight / 2);
    }

    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return <canvas ref={canvasRef} />;
}

useEffect(() => {
  const monitor1 = document.getElementById("monitor1");
  const monitor2 = document.getElementById("monitor2");

  const handleResize = () => {
    monitor1.style.width = window.innerWidth / 2 + "px";
    monitor1.style.height = window.innerHeight + "px";
    monitor1.style.left = "0";

    monitor2.style.width = window.innerWidth / 2 + "px";
    monitor2.style.height = window.innerHeight + "px";
    monitor2.style.right = "0";
  };

  handleResize();
  window.addEventListener("resize", handleResize);

  return () => {
    window.removeEventListener("resize", handleResize);
  };
}, []);

function App() {
  return (
    <div className="App">
      <div className="monitor">
        <HouseModel />
      </div>
      <div className="monitor">
        <h1>Hello World - Монитор 2</h1>
      </div>
    </div>
  );
}

export default App;
