import React, { Suspense, useRef } from "react";
import "./App.css";
//import FirstComponent from "./FirstComponent";
import SecondComponent from "./SecondComponent";
/* import { Canvas } from "react-three-fiber"; */
import { Canvas } from "@react-three/fiber";
import { OrbitControls } from "@react-three/drei";
import { useGLTF } from "@react-three/drei";

import House from "./models/House";

const App = () => {
  const groupRef = useRef(); // Ссылка на ваш <group> компонент

  // Функция для изменения масштаба модели
  const zoomIn = () => {
    if (groupRef.current) {
      groupRef.current.scale.multiplyScalar(1.1); // Увеличивает масштаб на 10%
    }
  };

  const zoomOut = () => {
    if (groupRef.current) {
      groupRef.current.scale.divideScalar(1.1); // Уменьшает масштаб на 10%
    }
  };
  return (
    <div className="app-container">
      <div className="first-component-container">
        <Canvas camera={{ position: [0, 2, 5], zoom: 1 }}>
          <OrbitControls />
          <color attach="background" args={["lightblue"]} />
          <hemisphereLight intensity={0.35} />
          <spotLight
            position={[10, 10, 10]}
            angle={0.3}
            penumbra={1}
            intensity={2}
            castShadow
          />
          <Suspense fallback={null}>
            <group ref={groupRef}>
              <House />
            </group>
          </Suspense>
          <axesHelper args={[3]} /> {/* Показывает ориентацию */}
          <mesh
            onClick={zoomIn} // При клике на модель увеличивается масштаб
            onContextMenu={(e) => {
              e.preventDefault();
              zoomOut(); // При правом клике на модель уменьшается масштаб
            }}
          >
            <boxBufferGeometry args={[1, 1, 1]} />
            <meshBasicMaterial color="transparent" opacity={0} />
          </mesh>
        </Canvas>
        {/* <FirstComponent /> */}
      </div>
      <div className="second-component-container">
        <SecondComponent />
      </div>
    </div>
  );
};

export default App;
