    if (groupRef.current) {
      groupRef.current.scale.multiplyScalar(1.1); // Увеличивает масштаб на 10%
    }
  };

  const zoomOut = () => {
    if (groupRef.current) {
      groupRef.current.scale.divideScalar(1.1); // Уменьшает масштаб на 10%
    }
  }; */

  const Controls = () => {
    const controls = useRef();

    const { camera, gl } = useThree();

    useFrame(() => {
      controls.current.update();
    });

    return (
      <orbitControls
        ref={controls}
        autoRotate
        args={[camera, gl.domElement]}
      ></orbitControls>
    );
  };
  return (
    <div className="app-container">
      <div className="first-component-container">
        {/* <FirstComponent /> */}
        <Canvas>
          <mesh>
            <Controls />

            <primitive
              object={<House />} // Используем new для создания экземпляра BoxGeometry
              attach="geometry"
            />
          </mesh>
        </Canvas>
      </div>
      <div className="second-component-container">
        <SecondComponent />
      </div>
    </div>
  );
};

export default App;

