import "./App.css";

import React, { useEffect, useRef } from "react";
import * as THREE from "three";

function App() {
  useEffect(() => {
    const monitor1 = document.getElementById("monitor1");
    const monitor2 = document.getElementById("monitor2");

    const handleResize = () => {
      monitor1.style.width = window.innerWidth / 2 + "px";
      monitor1.style.height = window.innerHeight + "px";
      monitor1.style.left = "0";

      monitor2.style.width = window.innerWidth / 2 + "px";
      monitor2.style.height = window.innerHeight + "px";
      monitor2.style.right = "0";
    };

    handleResize();
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <div className="container">
      <div className="monitor" id="monitor1">
        <h1>Hello World - Монитор 1</h1>
      </div>
      <div className="monitor" id="monitor2">
        <h1>Hello World - Монитор 2</h1>
      </div>
    </div>
  );
}

export default App;
