    if (groupRef.current) {
      groupRef.current.scale.divideScalar(1.1); // Уменьшает масштаб на 10%
    }
  };
  return (
    <div className="app-container">
      <div className="first-component-container">
        <FirstComponent />
      </div>
      <div className="second-component-container">
        <SecondComponent />
      </div>
    </div>
  );
};

export default App;
